import React, { Fragment, useState } from 'react';

const InputTodo = () => {

    const [application_name, setDescription] = useState("")

    const onSubmitForm = async() => {
        try{
            const body = { application_name };
            const response = await fetch("http://192.168.180.156:5000/application", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body)
        });
        window.location = "/";
        } catch (err) {
            console.error(err.message);

        }
    };

    return(
    <Fragment>
            <h1 className="text-center mt-5">ITDB Application Table List</h1>
            <form className="d-flex mt-5" onSubmit={onSubmitForm}>
                <input type="text" className="form-control" value={application_name} onChange={e => setDescription(e.target.value)}/>
                <button className="btn btn-success">Add</button>
            </form>

    </Fragment>);
};

export default InputApplication;
